<?php trait Cripto{protected function ot_read($file,$inside='no'){if($inside=='no'){$file=$this->container.'/'.$file;}else{$file=$this->container.'/'.$inside. '/'.$file;}$vread=[];if(file_exists($file)){$stream=fopen($file,"r");if($stream){$vread='';while(!feof($stream)){$vread.=fgets($stream);}$vread=json_decode($vread,true);fclose($stream);}else{$this->err='C0010M001';}}else{$this->err='C0010M005';}return $vread;}protected function ot_readif($file,$inside='no'){if($inside=='no'){$file=$this->container.'/'.$file;}else{$file=$this->container.'/'.$inside. '/'.$file;}$vread=[];if(file_exists($file)){$stream=fopen($file,"r");if($stream){$vread='';while(!feof($stream)){$vread.=fgets($stream);}$vread=json_decode($vread,true);fclose($stream);}else{$this->err='C0010M001';}}return $vread;}protected function ot_write($file,$data,$inside="no"){if($inside=='no'){$file=$this->container.'/'.$file;}else{$file=$this->container.'/'.$inside. '/'.$file;}$this->err='0';$stream=fopen($file,"w");if($stream){$save=fwrite($stream,json_encode($data,JSON_UNESCAPED_SLASHES));if($save){fclose($stream);}else{$this->err='C0010M003';}}else{$this->err='C0010M002';$this->errtext['C0010M002']='Failing create content';}return $this->err;}}